<script>
  import {publicPost} from '../api/index.js'
  import InfoOfChioce from '../components/InfoOfChioce.vue'
  export default {
    data() {
      return{
        articleList:[],
        count:0,
        onePage:8,
        articleNow:{},
      }
    },
    mounted(){
      this.getList(1);
      if(this.$route.params.id){
        this.getarticleNow(this.$route.params.id);
      }
    },
    methods:{
      getarticleNow(id){
        publicPost('home/getArticleContent',[{id:id}]).then((res) => {
          if(res.data.code == 0){
            var str = res.data.data.article_cover.bigcover.split('_bc')[0];
            this.articleNow = res.data.data;
            this.articleNow.pic = str;
            document.title = document.title + '-' + this.articleNow.article_title;
            this.$nextTick(() => {
              var html = this.articleNow.article_content.replace(/\\/g,'').replace(/src="/g,'src="https://api.amandacat.shop').replace(/src='/g,"src='https://api.amandacat.shop");
              document.getElementById('contentTxt').innerHTML = html;
            })
          }
        })
      },
      getList(pageNow){
        publicPost('home/getArticles',[{page:pageNow},{limit:this.onePage}]).then((res) => {
          if(res.data.code == 0){
            // article_class: "好物推荐"
            // article_cover: {bigcover: "cover/article_1591593504897/cover_20200608_80.jpg_bc.jpg",…}
            // bigcover: "cover/article_1591593504897/cover_20200608_80.jpg_bc.jpg"
            // smallcover: "cover/article_1591593504897/cover_20200608_80.jpg_sc.jpg"
            // article_description: "The CRM software giant reported​ non-GAAP earnings of 70 cents per share on revenue of $4.87 billion."
            // article_state: 1
            // article_title: "Salesforce beats Q1 estimates, lowers fiscal 2021 revenue growth forecast"
            // create_time: "2020-06-08 13:18:28"
            // id: 48
            // update_time: "2020-06-08 13:18:28"
            this.articleList = res.data.data.articles;
            this.count = Math.ceil(res.data.data.count/this.onePage);
          }
        })
      },
      change(e){
        this.getList(e);
      },

      gotoItems(id){
        var op = this.$router.resolve({
            name: 'Article',
            params: {
              id:id,
            }
          }
        )
        window.open(op.href,'_blank');
      },
    },
    components:{
      InfoOfChioce:InfoOfChioce,
    },
    name: 'Article'
  }
</script>

